sqlMap.select:
SELECT * FROM ( SELECT ROWNUM NUM, A.* FROM ( SELECT * FROM ( SELECT A.SEQ, A.TAG_NAME, A.RECOM_YN, NVL(B.CNT,0) CNT FROM PCN_MOV_TAGLIB A,( SELECT TAG_SEQ, COUNT(1) CNT FROM PCN_MOV_COMSTAG A, COMS_MOVIE_REG B WHERE A.COMS_IDX = B.IDX AND B.STATE = '3' GROUP BY TAG_SEQ ) B WHERE A.SEQ = B.TAG_SEQ(+)
AND TAG_NAME LIKE '%'||#keyword#||'%'
) A
ORDER BY CNT DESC
ORDER BY TAG_NAME
) A WHERE ROWNUM <= #enum# ) A WHERE NUM >= #snum#
