sqlMap.select:
SELECT R.* FROM ( SELECT FLOOR((ROW_NUMBER()OVER( ORDER BY A.SEQ DESC nulls last)-1)/$list_unit$+1) AS PAGE , A.SEQ, A.INUMBER, A.TITLE , '/upload/recom/recom/'||A.THUMB_URL IMAGE , '/culture/themeView.do?seq='||A.SEQ URL , B.NAME , (SELECT NAME FROM PCN_COMMON_CODE WHERE VALUE = TO_CHAR(B.TYPE) AND TYPE = 'WRITER_CD') SOURCE_NAME , A.REG_DATE , A.CONTENT AS SUMMARY FROM PCN_RECOM_CULTURE A, PORTAL_STORY_AUTHOR B WHERE A.APPROVAL = 'Y' AND A.CONT_TYPE = 'S' AND A.AUTHOR_SEQ = B.SEQ(+)
TITLE LIKE '%'||#searchKeyword#||'%'
) R WHERE R.PAGE = #page_no#

SELECT R.* FROM ( SELECT FLOOR((ROW_NUMBER()OVER( ORDER BY CUL_SEQ DESC)-1)/$list_unit$+1) AS PAGE , CUL_SEQ , CUL_GRP1 , CUL_GRP2 , CUL_NAME AS TITLE , CUL_CONT AS SUMMARY , DECODE(CUL_VIEWIMG1, '', '', '/upload/culSpace/' || CUL_VIEWIMG1) AS IMAGE , CUL_REGDATE AS CONT_DATE , CUL_PLACE || ' ' || CUL_PLACE2 AS PLACE , '/space/facilityView.do?seq=' || CUL_SEQ AS URL FROM VLI_VLI_CUL WHERE POST_FLAG = 'Y'
CUL_NAME LIKE '%' || #searchKeyword# || '%'
) R WHERE R.PAGE = #page_no#

SELECT R.* FROM ( SELECT FLOOR((ROW_NUMBER()OVER( ORDER BY SEQ DESC)-1)/$list_unit$+1) AS PAGE , SEQ , NAME AS TITLE , '/upload/group/' || IMG_FILE AS IMAGE , SUB_CONTENT AS SUMMARY , URL , ADDR1 || ' ' || ADDR2 AS PLACE , REG_DATE AS CONT_DATE FROM PCN_GROUP_CULTURE A WHERE APPROVAL = 'Y'
( NAME like '%'|| #searchKeyword# || '%' or DBMS_LOB.INSTR(CONTENT,#searchKeyword#) > 0 )
) R WHERE R.PAGE = #page_no#

SELECT * FROM ( SELECT ROWNUM NUM, A.* FROM ( SELECT URL , TITLE , TYPE , REG_DATE , UCI , REFERENCE_IDENTIFIER AS IMAGE , REG_DATE AS CONT_DATE , CREATOR AS RIGHTS FROM MV_RDF_METADATA_RELIC A WHERE NVL(APPROVAL, 'Y') = 'Y' AND REFERENCE_IDENTIFIER IS NOT NULL AND PUBLISHER != 'KCIC08'
TITLE LIKE '%'||#searchKeyword#||'%'
) A WHERE ROWNUM <= #enum# ) WHERE NUM >= #snum#

SELECT R.* FROM ( SELECT FLOOR((ROW_NUMBER()OVER(ORDER BY REG_DATE DESC nulls last)-1)/$list_unit$ + 1) AS PAGE , SEQ , TITLE , REG_DATE , CONTENT , ('http://www.culture.go.kr/event/winnerView.do?seq=' || SEQ) AS URL FROM CL_TOTAL_NEWS WHERE CONTENT_TYPE = 2
TITLE LIKE '%' || #searchKeyword# || '%'
) R WHERE R.PAGE = #page_no#

SELECT R.* FROM (SELECT FLOOR((ROW_NUMBER()OVER( ORDER BY SEQ DESC) -1)/$list_unit$+1) AS PAGE , A.SEQ AS seq, A.TITLE AS title, A.XTITLE AS rights, CONTENT AS summary, IMAGE_NAME AS image, 'D' AS code FROM PCN_PATTERN_DESIGN A WHERE STATUS = 'Y'
AND TITLE LIKE '%' || #search_word# || '%'
) R WHERE R.PAGE = #page_no#

SELECT * FROM (SELECT FLOOR((ROW_NUMBER()OVER( ORDER BY SEQ DESC) -1)/$list_unit$+1) AS PAGE , SEQ AS seq, TITLE AS title, PATTERN_NAME AS rights, IMAGE_NAME AS image, CONCEPT AS summary, 'G' AS code FROM PCN_PATTERN_GALLERY WHERE STATUS = 'Y'
AND TITLE LIKE '%' || #search_word# || '%'
) R WHERE R.PAGE = #page_no#

SELECT * FROM ( SELECT SEQ, TITLE, URL, FLOOR((ROW_NUMBER()OVER(ORDER BY reg_date DESC nulls last)-1)/$list_unit$+1) AS PAGE FROM PORTAL_CULTURE_SUPPORT WHERE 1=1 AND ( SYSDATE BETWEEN TO_DATE(APPLY_START_DT, 'yyyy-mm-dd') AND TO_DATE(APPLY_END_DT, 'yyyy-mm-dd') OR TO_DATE(APPLY_START_DT, 'YYYYMMDD') > SYSDATE) AND APPROVAL = 'Y'
( TITLE LIKE '%'|| #searchKeyword# ||'%' OR NOTICE LIKE '%'|| #searchKeyword# ||'%' )
) R WHERE R.PAGE = #page_no#
