SELECT R.* FROM ( SELECT /*+INDEX_DESC(A PORTAL_MAIN_CONTENT_PK)*/ FLOOR((ROW_NUMBER()OVER( ORDER BY A.REG_DATE DESC nulls last)-1)/$list_unit$+1) AS PAGE , A.SEQ , A.TITLE , DECODE( A.APPROVAL , 'Y' , '승인' , 'N' , '미승인' , '대기') AS APPROVAL , TO_CHAR(A.REG_DATE, 'YYYY-MM-DD') REG_DATE , WRITER , MAIN_TITLE , (SELECT MAX(URL) FROM PORTAL_MAIN_CONTENT_SUB B WHERE B.PSEQ = A.SEQ) AS URL FROM PORTAL_MAIN_CONTENT A WHERE CODE = #menu_type#
APPROVAL = #searchApproval#
) R WHERE R.PAGE = #page_no#

SELECT B.CODE, B.PCODE, B.NAME, DECODE(ROWNUM, 1, B.VALUE, DECODE(#menu_type#, '209', '', B.VALUE)) VALUE, A.* FROM ( SELECT B.* FROM PORTAL_MAIN_CONTENT A, PORTAL_MAIN_CONTENT_SUB B WHERE A.SEQ = B.PSEQ AND PSEQ = #seq#
)A,
( SELECT * FROM PORTAL_MAIN_CODE WHERE TYPE = 'MAIN_CONTENT' AND PCODE > 0 START WITH PCODE = #menu_type# CONNECT BY PRIOR CODE = PCODE ORDER SIBLINGS BY SORT ) B WHERE A.CODE(+) = B.CODE
