sqlMap.select:
select count(*) from ( ( SELECT A.USER_ID ,A.NAME ,A.EMAIL ,TO_CHAR(A.JOIN_DATE, 'YYYY-MM-DD') JOIN_DATE ,B.NAME JOIN_CATEGORY_NAME ,NVL(C.POINT, 0) POINT ,GRP_CER_FLAG ,SEC_CER_FLAG FROM PCN_MEMBER A ,PCN_COMMON_CODE B ,(SELECT USER_ID, SUM(POINT) AS POINT FROM PCN_CUSTOMER_POINT GROUP by USER_ID) C
)
minus
( SELECT A.USER_ID , A.NAME , A.EMAIL , TO_CHAR(A.JOIN_DATE, 'YYYY-MM-DD') JOIN_DATE , B.NAME JOIN_CATEGORY_NAME , NVL(C.POINT, 0) POINT , GRP_CER_FLAG, SEC_CER_FLAG FROM PCN_MEMBER A , PCN_COMMON_CODE B , (SELECT USER_ID, SUM(POINT) AS POINT FROM PCN_CUSTOMER_POINT GROUP BY USER_ID ) C WHERE A.AUTH <> 0 AND A.JOIN_CATEGORY = B.CODE(+) AND A.USER_ID = C.USER_ID(+) )
)

( SELECT A.USER_ID, A.NAME, A.EMAIL, A.JOIN_DATE, GRP_CER_FLAG, SEC_CER_FLAG, B.NAME JOIN_CATEGORY_NAME, NVL(C.POINT, 0) POINT FROM PCN_MEMBER A, PCN_COMMON_CODE B, (SELECT USER_ID, SUM(MILEAGE_POINT) AS POINT FROM MILDB.MILEAGE_LOG WHERE TO_CHAR(MILEAGE_DATE,'YYYY') >= TO_CHAR(SYSDATE,'YYYY') GROUP by USER_ID) C
) minus ( SELECT A.USER_ID, A.NAME, A.EMAIL, A.JOIN_DATE, GRP_CER_FLAG, SEC_CER_FLAG, B.NAME JOIN_CATEGORY_NAME, NVL(C.POINT, 0) POINT FROM PCN_MEMBER A, PCN_COMMON_CODE B , (SELECT USER_ID, SUM(MILEAGE_POINT) AS POINT FROM MILDB.MILEAGE_LOG WHERE TO_CHAR(MILEAGE_DATE,'YYYY') >= TO_CHAR(SYSDATE,'YYYY') GROUP by USER_ID) C WHERE A.AUTH <> 0 AND A.JOIN_CATEGORY = B.CODE(+) AND A.USER_ID = C.USER_ID(+) )
ORDER BY JOIN_DATE DESC NULLS LAST
